mapper.select:
select club_name as clubName,input_date as inputDate,view_cnt as viewCnt, ( select count(*) from INTEREST_LIST il where il.club_num=p.club_num) as interCnt,
AREA_NAME as addr, price,USER_IMG as userImg, detail_txt as detailTxt, club_addr as clubAddr, CATEGORY_NAME as categoryName, u.USER_ID as userId, PERSONAL_INTRO as personalIntro, LONGITUDE, LATITUDE,CLUB_DATE as clubDate,NUMBER_PEOPLE as numberPeople, nickname as nickName,
( select count(*) from club_review cc where p.club_num=cc.club_num) as reviewCnt from club p, users u, ACTIVITY_AREA aa, club_CATEGORY pc where u.user_id=p.user_id and p.CATEGORY_NUM=pc.CATEGORY_NUM and p.ACTI_AREA_NUM=aa.ACTI_AREA_NUM and club_num=#{club_Num}

mapper.update:
  	update CLUB
	set view_cnt=view_cnt+1
	where club_num=#{club_Num}

mapper.select:
  	select		club_IMG
	from		club_IMG
	where		club_num=#{club_num}

select USER_ID
from INTEREST_LIST
where user_id=#{user_id} and club_num=#{club_Num}

select user_id
from JOIN_LIST
where user_id=#{user_id} and club_num=#{club_Num}

  	select user_id
	from APPROVAL_STATUS
	where user_id=#{user_id} and club_num=#{club_Num}

mapper.update:
update 	club
set input_date=sysdate		

	,CLUB_NAME=#{CLUB_NAME}


	,DETAIL_TXT=#{DETAIL_TXT}


	,NUMBER_PEOPLE={NUMBER_PEOPLE}


	,PRICE like=#{PRICE}


	,CLUB_DATE like=#{CLUB_DATE}


	,CLUB_ADDR like=#{CLUB_ADDR}


	,LONGITUDE like=#{LONGITUDE}


	,LATITUDE like=#{LATITUDE}
		
where 	club_num=#{club_num}

mapper.select:
select 	club_name 
from 	club
where 	club_num=#{club_num}
